            override fun onResponse(call: Call, response: Response) {
                if (response.isSuccessful) {
                    callback(true, null)
                } else {
                    val errorBody = response.body.string()
                    callback(false, "Error HTTP ${response.code}: $errorBody")
                }
            }
        })
    }

