package com.plyr.examples

import android.Manifest
import android.content.pm.PackageManager
import android.os.Build
import androidx.activity.ComponentActivity
import androidx.activity.result.contract.ActivityResultContracts
import androidx.core.content.ContextCompat
import androidx.lifecycle.lifecycleScope
import com.plyr.service.MusicNotificationManager
import com.plyr.viewmodel.PlayerViewModel
import kotlinx.coroutines.launch

/**
 * Ejemplo de integración del sistema de notificaciones de música
 * 
 * Este archivo muestra cómo implementar las notificaciones en tu MainActivity.
 * Copia este código a tu MainActivity existente.
 */
class NotificationIntegrationExample {

    // En tu MainActivity, agrega estas propiedades:
    private lateinit var notificationManager: MusicNotificationManager
    private lateinit var playerViewModel: PlayerViewModel

    // Launcher para pedir permisos de notificación (Android 13+)
    private val notificationPermissionLauncher = registerForActivityResult(
        ActivityResultContracts.RequestPermission()
    ) { isGranted ->
        if (isGranted) {
            // Permiso concedido, continuar con la inicialización
            initializeNotificationSystem()
        } else {
            // Permiso denegado, manejar el caso apropiadamente
            // Puedes mostrar un mensaje al usuario explicando por qué necesitas el permiso
        }
    }

    /**
     * Llama este método en onCreate() de tu MainActivity
     */
    fun setupNotificationSystem(activity: ComponentActivity, viewModel: PlayerViewModel) {
        playerViewModel = viewModel

        // Verificar y pedir permisos de notificación para Android 13+
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.TIRAMISU) {
            when {
                ContextCompat.checkSelfPermission(
                    activity,
                    Manifest.permission.POST_NOTIFICATIONS
                ) == PackageManager.PERMISSION_GRANTED -> {
                    // Permiso ya concedido
                    initializeNotificationSystem()
                }
                else -> {
                    // Pedir permiso
                    notificationPermissionLauncher.launch(Manifest.permission.POST_NOTIFICATIONS)
                }
            }
        } else {
            // Android 12 y menores no necesitan permiso explícito
            initializeNotificationSystem()
        }
    }

    /**
     * Inicializa el sistema de notificaciones
     */
    private fun initializeNotificationSystem() {
        notificationManager = MusicNotificationManager(this)
        notificationManager.startService()

        // Configurar listeners para actualizar notificación cuando cambie el track
        setupNotificationListeners()
    }

    /**
     * Configura los listeners para sincronizar el PlayerViewModel con las notificaciones
     */
    private fun setupNotificationListeners() {
        // Observar cambios en el track actual
        playerViewModel.currentTrack.observe(this) { track ->
            track?.let {
                notificationManager.updateSongInfo(it.name, it.artists)
            }
        }

        // Observar cambios en el título actual
        playerViewModel.currentTitle.observe(this) { title ->
            title?.let {
                // Si tienes información del artista disponible
                val artist = "Artista Desconocido" // Reemplaza con la fuente real del artista
                notificationManager.updateSongInfo(it, artist)
            }
        }
    }

    /**
     * Ejemplo de cómo reproducir una canción con notificación
     */
    fun playTrackWithNotification(audioUrl: String, title: String, artist: String) {
        lifecycleScope.launch {
            // Actualizar información en la notificación
            notificationManager.updateSongInfo(title, artist)

            // Reproducir el audio en el servicio de notificación
            notificationManager.playAudio(audioUrl, title, artist)

            // También reproducir en tu PlayerViewModel existente
            // playerViewModel.playAudio(audioUrl) // Tu método existente
        }
    }

    /**
     * Limpieza en onDestroy()
     */
    fun cleanupNotificationSystem() {
        if (::notificationManager.isInitialized) {
            notificationManager.stopService()
        }
    }
}

/**
