/**
 * Extensiones para integrar PlayerViewModel con notificaciones de música
 * 
 * Estas extensiones facilitan el uso del sistema de notificaciones
 * sin modificar directamente el PlayerViewModel existente.
 */

/**
 * Inicializa el manager de notificaciones
 */
fun PlayerViewModel.initializeNotifications(context: Context): MusicNotificationManager {
    return MusicNotificationManager(context).apply {
        startService()
    }
}

/**
 * Reproduce un track con notificación
 */
fun PlayerViewModel.playWithNotification(
    notificationManager: MusicNotificationManager,
    track: TrackEntity,
    onAudioUrlReady: (String) -> Unit
) {
    // Actualizar la información del track en la notificación
    notificationManager.updateSongInfo(track)

    // Tu lógica existente de reproducción aquí
    // Cuando tengas la URL de audio, llamar:
    // onAudioUrlReady(audioUrl)
}

/**
 * Actualiza la notificación cuando cambia el track
 */
fun PlayerViewModel.updateNotificationTrack(
    notificationManager: MusicNotificationManager,
    track: TrackEntity?
) {
    track?.let {
        notificationManager.updateSongInfo(it.name, it.artists)
    }
}
