package com.plyr.service

import android.content.ComponentName
import android.content.Context
import android.content.Intent
import android.content.ServiceConnection
import android.os.IBinder
import android.util.Log
import com.plyr.database.TrackEntity

/**
 * MusicNotificationManager - Clase helper para gestionar la notificación de música
 * 
 * Esta clase actúa como puente entre tu PlayerViewModel y el MusicService,
 * facilitando el control de la notificación sin tener que manejar directamente
 * el service binding en el ViewModel.
 */
class MusicNotificationManager(private val context: Context) {

    private var musicService: MusicService? = null
    private var isBound = false

    companion object {
        private const val TAG = "MusicNotificationManager"
    }

    /**
     * Conexión con el servicio de música
     */
    private val serviceConnection = object : ServiceConnection {
        override fun onServiceConnected(name: ComponentName?, service: IBinder?) {
            Log.d(TAG, "Service connected")
            val binder = service as MusicService.MusicBinder
            musicService = binder.getService()
            isBound = true
        }

        override fun onServiceDisconnected(name: ComponentName?) {
            Log.d(TAG, "Service disconnected")
            musicService = null
            isBound = false
        }
    }

    /**
     * Inicia el servicio y se conecta a él
     */
    fun startService() {
        Log.d(TAG, "Starting MusicService")
        val serviceIntent = Intent(context, MusicService::class.java)
        context.startService(serviceIntent)
        context.bindService(serviceIntent, serviceConnection, Context.BIND_AUTO_CREATE)
    }

    /**
     * Para el servicio y se desconecta
     */
    fun stopService() {
        Log.d(TAG, "Stopping MusicService")
        if (isBound) {
            context.unbindService(serviceConnection)
            isBound = false
        }

        val serviceIntent = Intent(context, MusicService::class.java)
        context.stopService(serviceIntent)
        musicService = null
    }

    /**
     * Inicia la reproducción de audio con notificación
     */
    fun playAudio(audioUrl: String, title: String, artist: String) {
        Log.d(TAG, "Playing audio: $title by $artist")

        if (!isBound) {
            // Si no está conectado, iniciar el servicio con los datos
            val serviceIntent = Intent(context, MusicService::class.java).apply {
                putExtra("AUDIO_URL", audioUrl)
                putExtra("SONG_TITLE", title)
                putExtra("SONG_ARTIST", artist)
            }
            context.startService(serviceIntent)
            context.bindService(serviceIntent, serviceConnection, Context.BIND_AUTO_CREATE)
        } else {
            // Si ya está conectado, usar el servicio directamente
            musicService?.updateSongInfo(title, artist)
            musicService?.playAudio(audioUrl)
        }
    }

    /**
     * Actualiza la información de la canción en la notificación
     */
    fun updateSongInfo(title: String, artist: String) {
        Log.d(TAG, "Updating song info: $title by $artist")

        if (isBound && musicService != null) {
            musicService?.updateSongInfo(title, artist)
        } else {
            // Si no está conectado, enviar intent de actualización
            val serviceIntent = Intent(context, MusicService::class.java).apply {
                putExtra("UPDATE_INFO", true)
                putExtra("SONG_TITLE", title)
                putExtra("SONG_ARTIST", artist)
            }
            context.startService(serviceIntent)
        }
    }

    /**
     * Actualiza usando un TrackEntity
     */
    fun updateSongInfo(track: TrackEntity) {
        updateSongInfo(track.name, track.artists)
    }

    /**
     * Pausa/reanuda la reproducción desde la notificación
     */
    fun togglePlayPause() {
        musicService?.togglePlayPause()
    }

    /**
     * Para completamente la reproducción
     */
    fun stopPlayback() {
        musicService?.stopPlayback()
    }

    /**
     * Verifica si está reproduciendo
     */
    fun isPlaying(): Boolean {
        return musicService?.isPlaying() ?: false
    }

    /**
     * Verifica si el servicio está conectado
     */
    fun isServiceConnected(): Boolean {
        return isBound && musicService != null
    }
}

package com.plyr.ui.components.search

import androidx.compose.foundation.clickable
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.material3.*
